import React, { Component } from 'react';
import { View, Text, TextInput, TouchableOpacity } from 'react-native';

export default class All extends Component {  
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],   //lista goala de sarcini
      newTask: '', //camp text gol pt a adauga o noua sarcina
      completedTasks: [], //lista taskuri finalizate
    };
  }

  ///se ocupa cu adaugarea unei noi sarcini la lista de sarcini
  ///ia ca argument starea curenta a componentei si verifica daca
  //campul de text newTask nu este gol
  ///Daca e nevoie, o adauga la noua lista de tasks si goleste campul de text
  addTask = () => {
    const { tasks, newTask } = this.state;
    if (newTask.trim() !== '') {
      this.setState({
        tasks: [...tasks, newTask],
        newTask: '',
      });
    }
  };


  ///se ocupa cu marcarea/demarcarea unui task ca finalizata
  ///verifica taskul daca e finalizat 
  ///la final actualizeaza lista completedTasks
  VerifTasks = (index) => {
    const { completedTasks } = this.state;
    const newCompletedTasks = [...completedTasks];
    if (newCompletedTasks.includes(index)) {
      const indexToRemove = newCompletedTasks.indexOf(index);
      newCompletedTasks.splice(indexToRemove, 1);
    } else {
      newCompletedTasks.push(index);
    }
    this.setState({ completedTasks: newCompletedTasks });
  };


  //ia ca argument starea curenta a componentei si afiseaza elementele ui din starile acelea
  render() {
    const { tasks, newTask, completedTasks } = this.state;

    return (
      <View style={styles.container}>
        <View style={[styles.elementsContainer, styles.purple]}>
          <Text style={styles.text}>HELLO, STUDENT!</Text>
          <Text style={styles.text_data}>19.12.2023</Text>
        </View>
        <View style={[styles.elementsContainer, styles.white]}>
          <Text style={styles.task}>TASKS</Text>
          <View style={[styles.elementsContainer, styles.purple3]}>
        </View>
          {tasks.map((task, index) => (
            <TouchableOpacity
              key={index}
              style={styles.taskItem}
              onPress={() => this.VerifTasks(index)}
            >
              <Text style={styles.checkbox}>
                {completedTasks.includes(index) ? '‚òëÔ∏è' : 'üî≤'}
              </Text>
              <Text style={[styles.taskText, completedTasks.includes(index) && styles.completedTask]}>
                {task}
              </Text>
            </TouchableOpacity>
          ))}
          <TextInput
            style={styles.input}
            value={newTask}
            onChangeText={(text) => this.setState({ newTask: text })}
            placeholder="Adauga un nou task..."
          />
          <TouchableOpacity style={styles.addButton} onPress={this.addTask}>
            <Text style={styles.addButtonText}>ADD TASK</Text>
          </TouchableOpacity>
        </View>
      </View>
    );
  }
}

const styles = {
  text2: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    marginTop: 100,
  },
  elementsContainer: {
    width: 370,
    marginBottom: 24,
    borderRadius: 8,
    alignItems: 'left',
  },
  white: {
    backgroundColor: '#FFFFFF',
    padding: 20,
  },
  purple: {
    backgroundColor: 'pink',
    height: 90,
  },
  purple2: {
    backgroundColor: 'pink',
    height: 40,
  },
  purple3: {
    backgroundColor: 'pink',
    height: 2,
  },
  text: {
    fontSize: 40,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white',
  },
  text_data: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white',
  },
  task: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'black',
  },
  taskItem: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  checkbox: {
    fontSize: 20,
    marginRight: 20,
  },
  taskText: {
    fontSize: 16,
  },
  completedTask: {
    textDecorationLine: 'line-through',
    color: 'black',
  },
  input: {
    height: 50,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 10,
    backgroundColor: '#FFFFFF',
    width: '100%',
  },
  addButton: {
    backgroundColor: 'black',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    alignItems: 'center',
  },
  addButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
};
