import React, { Component } from 'react';
import { View, Text, TextInput, TouchableOpacity } from 'react-native';

export default class ToDoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasksList: [],
      newTaskInput: '',
      completedTasksList: [],
    };
  }

  handleAddTask = () => {
    const { tasksList, newTaskInput } = this.state;
    if (newTaskInput.trim() !== '') {
      this.setState({
        tasksList: [...tasksList, newTaskInput],
        newTaskInput: '',
      });
    }
  };

  handleTaskCompletion = (index) => {
    const { completedTasksList } = this.state;
    const newCompletedTasksList = [...completedTasksList];
    if (newCompletedTasksList.includes(index)) {
      const indexToRemove = newCompletedTasksList.indexOf(index);
      newCompletedTasksList.splice(indexToRemove, 1);
    } else {
      newCompletedTasksList.push(index);
    }
    this.setState({ completedTasksList: newCompletedTasksList });
  };

  renderHeader = () => (
    <View style={[styles.elementsContainer, styles.purple]}>
      <Text style={styles.text}>HELLO, STUDENT!</Text>
      <Text style={styles.textData}>9.01.2024</Text>
    </View>
  );

  renderTasks = () => (
    <View style={[styles.elementsContainer, styles.white]}>
      <Text style={styles.task}>TASKS</Text>
      <View style={[styles.elementsContainer, styles.purple3]}></View>
      {this.renderTaskList()}
      {this.renderTaskInput()}
    </View>
  );

  renderTaskList = () =>
    this.state.tasksList.map((task, index) => (
      <TouchableOpacity
        key={index}
        style={styles.taskItem}
        onPress={() => this.handleTaskCompletion(index)}
      >
        <Text style={styles.checkbox}>
          {this.state.completedTasksList.includes(index) ? '☑️' : '🔲'}
        </Text>
        <Text
          style={[
            styles.taskText,
            this.state.completedTasksList.includes(index) && styles.completedTask,
          ]}
        >
          {task}
        </Text>
      </TouchableOpacity>
    ));

  renderTaskInput = () => (
    <>
      <TextInput
        style={styles.input}
        value={this.state.newTaskInput}
        onChangeText={(text) => this.setState({ newTaskInput: text })}
        placeholder="Add a new task..."
      />
      <TouchableOpacity style={styles.addButton} onPress={this.handleAddTask}>
        <Text style={styles.addButtonText}>ADD TASK</Text>
      </TouchableOpacity>
    </>
  );

  render() {
    return (
      <View style={styles.container}>
        {this.renderHeader()}
        {this.renderTasks()}
      </View>
    );
  }
}

const styles = {
  container: {
    flex: 1,
    alignItems: 'center',
    marginTop: 100,
  },
  elementsContainer: {
    width: 370,
    marginBottom: 24,
    borderRadius: 8,
    alignItems: 'left',
  },
  white: {
    backgroundColor: '#FFFFFF',
    padding: 20,
  },
  purple: {
    backgroundColor: 'pink',
    height: 90,
  },
  purple3: {
    backgroundColor: 'pink',
    height: 2,
  },
  text: {
    fontSize: 40,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white',
  },
  textData: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white',
  },
  task: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'black',
  },
  taskItem: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  checkbox: {
    fontSize: 20,
    marginRight: 20,
  },
  taskText: {
    fontSize: 16,
  },
  completedTask: {
    textDecorationLine: 'line-through',
    color: 'black',
  },
  input: {
    height: 50,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 10,
    backgroundColor: '#FFFFFF',
    width: '100%',
  },
  addButton: {
    backgroundColor: 'black',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    alignItems: 'center',
  },
  addButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
};
